module.exports = function render(api) {

  return function(req, res, next) {

    res.setHeader('Content-Type', req.api.request.format);

    switch(req.api.request.format) {

      case 'application/json':
        res.api.content = JSON.stringify(res.api.content);
        break;

      case 'text/plain':
      default: // we need a string at the end!
        res.api.content = JSON.stringify(res.api.content);
        break;
    }
 
    next();
  }

}